import React, { Component, useContext } from 'react';
import { Link, withRouter, useLocation } from "react-router-dom";
import { ProductContext } from '../../Context/ProductContext/ProductContext';
import './Navigation.css';

// Material-UI

import AppBar from '@material-ui/core/AppBar';
import Toolbar from '@material-ui/core/Toolbar';
import IconButton from '@material-ui/core/IconButton';
import Typography from '@material-ui/core/Typography';
import InputBase from '@material-ui/core/InputBase';
import Badge from '@material-ui/core/Badge';
import { fade, makeStyles, withStyles } from '@material-ui/core/styles';


import clsx from 'clsx';
import Drawer from '@material-ui/core/Drawer';
import List from '@material-ui/core/List';
import Divider from '@material-ui/core/Divider';
import ChevronRightIcon from '@material-ui/icons/ChevronRight';
import ListItem from '@material-ui/core/ListItem';
import ListItemIcon from '@material-ui/core/ListItemIcon';
import ListItemText from '@material-ui/core/ListItemText';

import MenuIcon from '@material-ui/icons/Menu';
import HomeIcon from '@material-ui/icons/Home';
import SearchIcon from '@material-ui/icons/Search';
import ShoppingCartIcon from '@material-ui/icons/ShoppingCart';
import PersonAddIcon from '@material-ui/icons/PersonAdd';
import PersonIcon from '@material-ui/icons/Person';
import InfoIcon from '@material-ui/icons/Info';
import FormatQuoteIcon from '@material-ui/icons/FormatQuote';
import SettingsIcon from '@material-ui/icons/Settings';
import BallotIcon from '@material-ui/icons/Ballot';


const drawerWidth = 240;

const useStyles = makeStyles((theme) => ({
    root: {
        display: 'flex',
        flexGrow: 1,
        overflow: 'hidden',
    },
    menuButton: {
        marginRight: theme.spacing(2),
    },
    title: {
        flexGrow: 1,
        display: 'none',
        [theme.breakpoints.up('sm')]: {
            display: 'block',
        },
    },
    search: {
        position: 'relative',
        borderRadius: theme.shape.borderRadius,
        backgroundColor: fade(theme.palette.common.white, 0.15),
        '&:hover': {
            backgroundColor: fade(theme.palette.common.white, 0.25),
        },
        marginLeft: 0,
        width: '100%',
        [theme.breakpoints.up('sm')]: {
            marginLeft: theme.spacing(1),
            width: 'auto',
        },
    },
    searchIcon: {
        padding: theme.spacing(0, 2),
        height: '100%',
        position: 'absolute',
        pointerEvents: 'none',
        display: 'flex',
        alignItems: 'center',
        justifyContent: 'center',
    },
    inputRoot: {
        color: 'inherit',
    },
    inputInput: {
        padding: theme.spacing(1, 1, 1, 0),
        // vertical padding + font size from searchIcon
        paddingLeft: `calc(1em + ${theme.spacing(4)}px)`,
        transition: theme.transitions.create('width'),
        width: '100%',
        [theme.breakpoints.up('sm')]: {
            width: '10ch',
            '&:focus': {
                width: '20ch',
            },
        },
    },
    appBar: {
        transition: theme.transitions.create(['margin', 'width'], {
            easing: theme.transitions.easing.sharp,
            duration: theme.transitions.duration.leavingScreen,
        }),
    },
    appBarShift: {
        width: `calc(100% - ${drawerWidth}px)`,
        marginLeft: drawerWidth,
        transition: theme.transitions.create(['margin', 'width'], {
            easing: theme.transitions.easing.easeOut,
            duration: theme.transitions.duration.enteringScreen,
        }),
    },
    menuButton: {
        marginRight: theme.spacing(2),
    },
    hide: {
        display: 'none',
    },
    drawer: {
        width: drawerWidth,
        flexShrink: 0,
    },
    drawerPaper: {
        width: drawerWidth,
    },
    drawerHeader: {
        display: 'flex',
        alignItems: 'center',
        padding: theme.spacing(0, 1),
        // necessary for content to be below app bar
        ...theme.mixins.toolbar,
        justifyContent: 'flex-end',
    },
    content: {
        flexGrow: 1,
        padding: theme.spacing(3),
        transition: theme.transitions.create('margin', {
            easing: theme.transitions.easing.sharp,
            duration: theme.transitions.duration.leavingScreen,
        }),
        marginLeft: -drawerWidth,
    },
    contentShift: {
        transition: theme.transitions.create('margin', {
            easing: theme.transitions.easing.easeOut,
            duration: theme.transitions.duration.enteringScreen,
        }),
        marginLeft: 0,
    },
    active: {
        color: theme.palette.primary.main,
    }
}
));

const StyledBadge = withStyles((theme) => ({
    badge: {
        right: -3,
        top: 13,
        border: `2px solid ${theme.palette.background.paper}`,
        padding: '0 4px',
    },
}))(Badge);

function Navigation() {
    const classes = useStyles();
    const location = useLocation();
    const [products, setProducts] = useContext(ProductContext);
    const [open, setOpen] = React.useState(false);

    const handleDrawerOpen = () => {
        setOpen(true);
    };

    const handleDrawerClose = () => {
        setOpen(false);
    };

    const [cart, setCart] = React.useState(0);
    const incrementCart = () => {
        setCart(cart + 1);
        handleDrawerClose();
    };

    return (
        <div className={classes.root}>
            <AppBar
                position="sticky"
                className={clsx(classes.appBar, {
                    [classes.appBarShift]: open,
                })}>
                <Toolbar>
                    <IconButton
                        edge="start"
                        className={clsx(classes.menuButton, open && classes.hide)}
                        onClick={handleDrawerOpen}
                        color="inherit"
                        aria-label="open drawer"
                    >
                        <MenuIcon />
                    </IconButton>
                    <Typography className={classes.title} variant="h6" noWrap>
                        Web Shop
            </Typography>
                    <div
                        className={classes.search}
                        onClick={handleDrawerClose}>
                        <div className={classes.searchIcon}>
                            <SearchIcon />
                        </div>
                        <InputBase
                            placeholder="Searchâ€¦"
                            classes={{
                                root: classes.inputRoot,
                                input: classes.inputInput,
                            }}
                            inputProps={{ 'aria-label': 'search' }}
                        />
                    </div>
                </Toolbar>
            </AppBar>

            <Drawer
                className={classes.drawer}
                variant="persistent"
                anchor="left"
                open={open}
                classes={{
                    paper: classes.drawerPaper,
                }}
            >
                <div className={classes.drawerHeader}>
                    <IconButton onClick={handleDrawerClose}>
                        <ChevronRightIcon />
                    </IconButton>
                </div>

                <Divider />

                <List>
                    <Link to="/" onClick={handleDrawerClose}>
                        <ListItem button>
                            <ListItemIcon><HomeIcon /></ListItemIcon>
                            <ListItemText 
                            primary={'Home'} 

                            />
                        </ListItem>

                    </Link>
                    <Link to="/products" onClick={handleDrawerClose}>
                        <ListItem button>
                            <ListItemIcon><BallotIcon /></ListItemIcon>
                            <ListItemText 
                            primary={'Products'} 
                            className={(location.pathname==='/register'?classes.active:'')}
                            />
                        </ListItem>
                    </Link>
                </List>
                <Divider />
                <List>
                    <Link to="/login" onClick={handleDrawerClose}>
                        <ListItem button>
                            <ListItemIcon><PersonIcon /></ListItemIcon>
                            <ListItemText 
                            primary={'Log In'}
                            className={(location.pathname==='/register'?classes.active:'')}
                            />
                        </ListItem>
                    </Link>
                    <Link to="/register" onClick={handleDrawerClose}>
                        <ListItem button>
                            <ListItemIcon><PersonAddIcon /></ListItemIcon>
                            <ListItemText
                            primary={'Register'}
                            className={(location.pathname==='/register'?classes.active:'')}
                            />
                        </ListItem>
                    </Link>
                    <Link to="/mycart" onClick={incrementCart}>
                        <ListItem button>
                            <ListItemIcon>
                                <StyledBadge badgeContent={products.length} color="secondary">
                                    <ShoppingCartIcon />
                                </StyledBadge>
                            </ListItemIcon>
                            <ListItemText primary={'My Cart'} />
                        </ListItem>
                    </Link>
                </List>
                <Divider />
                <List>
                    <Link to="/rubrics" onClick={handleDrawerClose}>
                        <ListItem button>
                            <ListItemIcon><InfoIcon /></ListItemIcon>
                            <ListItemText primary={'Rubrics'} />
                        </ListItem>
                    </Link>
                    <Link to="/references" onClick={handleDrawerClose}>
                        <ListItem button>
                            <ListItemIcon><FormatQuoteIcon /></ListItemIcon>
                            <ListItemText primary={'References'} />
                        </ListItem>
                    </Link>
                </List>
                <Divider />
                <List>
                    <Link to="/preferences">
                        <ListItem button onClick={handleDrawerClose}>
                            <ListItemIcon><SettingsIcon /></ListItemIcon>
                            <ListItemText primary={'Preferences'} />
                        </ListItem>
                    </Link>
                </List>
            </Drawer>
        </div >
    );
}

export default withRouter(Navigation);
