import React, { Component, useState, useContext } from 'react';
import CustomCard from '../../Components/CustomCard/CustomCard';
import ItemsGrid from '../../Components/ItemsGrid/ItemsGrid';
import { ProductContext } from '../../Context/ProductContext/ProductContext';
import { UserContext } from '../../Context/UserContext/UserContext';

import { makeStyles, useTheme } from '@material-ui/core/styles';
import MobileStepper from '@material-ui/core/MobileStepper';
import Paper from '@material-ui/core/Paper';
import Typography from '@material-ui/core/Typography';
import Button from '@material-ui/core/Button';
import KeyboardArrowLeft from '@material-ui/icons/KeyboardArrowLeft';
import KeyboardArrowRight from '@material-ui/icons/KeyboardArrowRight';
import Grid from '@material-ui/core/Grid';
import ImageSlider from '../../Components/ImageSlider/ImageSlider';
import Container from '@material-ui/core/Container';
import Breadcrumbs from '@material-ui/core/Breadcrumbs';
import Main from '../../Components/Main/Main';
import ShoppingCartIcon from '@material-ui/icons/ShoppingCart';
import ShoppingBasketIcon from '@material-ui/icons/ShoppingBasket';
import { ButtonGroup } from 'reactstrap';
import InfoTable from '../../Components/InfoTable/InfoTable';
import Spacer from '../../Components/Spacer/Spacer';
import { Link, withRouter, useLocation } from "react-router-dom";
import { classes } from 'istanbul-lib-coverage';


function SimpleBreadcrumbs(props) {
    return (
        <Breadcrumbs aria-label="breadcrumb">
            <Link to="/" className={classes.breadLink} color="inherit">
            <Typography>Home</Typography>

        </Link>
            <Link to="/products" className={classes.breadLink} color="inherit">
                <Typography>Products</Typography>
        </Link>
            <Typography color="textPrimary">{props.value}</Typography>
        </Breadcrumbs>
    );
}

const useStyles = makeStyles((theme) => ({
    images: {
        marginTop: '1rem',
    },
    content: {
        margin: '1rem 0',
        display: 'flex',
        alignItems: 'center',
        justifyContent: 'center',
    },
    spacing: {
        margin: '0.2rem',
        padding: '0.6rem',
    },
    spacer: {
        marginLeft: 'auto',
    },
    price: {
        width: '100%',
        padding: '1rem',
    },
    breadLink: {
        color: '#333',
        textDecoration: 'none',
    }
}));


const Product = (props) => {
    const classes = useStyles();
    const [products, setProducts] = useContext(ProductContext);
    const [user, setUser] = useContext(UserContext);

    const { params } = props.match;
    //const product = findProduct(products, params.id);
    const product = products[params.id];

    return (

        <Container maxWidth='md'>
            <Paper square elevation={0}>
                <Grid container>
                    <Grid item className={classes.spacing} xs={12} md={6}>
                        <Typography variant='h4' >{product.name}</Typography>
                        <Breadcrumbs aria-label="breadcrumb">
            <Link to="/" className={classes.breadLink} color="inherit">
            <Typography>Home</Typography>

        </Link>
            <Link to="/products" className={classes.breadLink} color="inherit">
                <Typography>Products</Typography>
        </Link>
            <Typography color="textPrimary">{props.value}</Typography>
        </Breadcrumbs><Grid item xs className={classes.images}>
                            <ImageSlider images={product.images} />
                        </Grid>
                    </Grid>


                    <Grid item xs={12} md={5} className={classes.content}>
                        <Paper elevation={3} className={classes.price}>
                            <Grid container>
                                <Grid item xs={12}>
                                    <Typography variant='h5'>
                                        {product.price}$
                                </Typography>
                                </Grid>
                                <Grid container>


                                    <Grid item xs={6}>
                                        <Button
                                            color="primary"
                                            startIcon={<ShoppingCartIcon />}
                                        >
                                            Add to cart
                                                    </Button>
                                    </Grid>
                                    <Grid item xs={6} align='right'>
                                        <Button
                                            variant="contained"
                                            color="primary"
                                            startIcon={<ShoppingBasketIcon />}
                                            className={classes.spacer}
                                        >
                                            Buy now
                                                </Button>
                                    </Grid>

                                </Grid>

                            </Grid>
                        </Paper>

                    </Grid>
                    <Grid item className={classes.spacing}>
                        <Typography variant='h5'>
                            Description
                        </Typography>
                        <Typography>{product.description}</Typography>
                        <Typography variant='h5'>

                        </Typography>
                    </Grid>
                </Grid>
            </Paper>
            {
                product.table?<InfoTable rows={product.table} />:''
            }

            <Spacer />
        </Container>


    );
}

export default withRouter(Product);
