import React, {useState} from 'react';
import { makeStyles, withStyles } from '@material-ui/core/styles';
import {UserContext} from '../../Context/UserContext/UserContext';

import CssBaseline from '@material-ui/core/CssBaseline';
import Grid from '@material-ui/core/Grid';
import Typography from '@material-ui/core/Typography';
import Container from '@material-ui/core/Container';
import Paper from '@material-ui/core/Paper';
import Main from '../../Components/Main/Main';
import Stepper from '../../Components/Stepper/Stepper';
import TextField from '@material-ui/core/TextField';
import Button from '@material-ui/core/Button';
import FormControlLabel from '@material-ui/core/FormControlLabel';
import Checkbox from '@material-ui/core/Checkbox';

const useStyles = makeStyles((theme) => ({
    root: {
        width: '100%',
    },
    button: {
        marginRight: theme.spacing(1),
    },
    buttons: {
        display: 'flex',
        justifyContent: 'flex-end',
    },
    margin: {
        marginBottom: theme.spacing(2),
    },
}));

function Register() {
    const classes = useStyles();
    const [checked, setChecked] = useState(false);
    const [user, setUser] = useContext(UserContext);

    const handleCheck = () => {
        setChecked(!checked);
    }

    const handleRegister = () => {
        if (!checked) return;
        setUser({
            loggedIn: true,
            username: 'mbreka',
            password: '',
            liked: [],
            cart: [],
        });
    };
    return (
        <Main >
            <Grid className={classes.root}>
                <Grid item xs className={classes.margin} >
                    <Typography variant="h5" component="h2">
                        Register
                    </Typography>
                </Grid>
                <form>
                    <Grid item xs>
                        <TextField className={classes.margin} label="Username" fullWidth type="text" variant="outlined" />
                    </Grid>
                    <Grid item xs className={classes.margin}>
                        <TextField className={classes.margin} label="Password" fullWidth type="password" variant="outlined" />
                    </Grid>
                </form>
                <Grid item className={classes.margin}>
                    <FormControlLabel
                        control={
                            <Checkbox
                                checked={checked}
                                onChange={handleCheck}
                                name="check"
                                color="primary"
                            />
                        }
                        label="I agree to terms of service."
                    />
                </Grid>
                <Grid container xs className={classes.buttons}>
                    <Grid item xs>
                        <Button className={classes.button} >I have an account</Button>
                    </Grid>
                    <Grid item>
                        <Button variant="contained" color="primary" className={classes.button}>Register</Button>
                    </Grid>
                </Grid>
            </Grid>
        </Main>
    );
}

export default Register;
