import React, { useState, useContext } from 'react';
import { Redirect } from 'react-router-dom';
import { makeStyles, withStyles } from '@material-ui/core/styles';
import Card from '@material-ui/core/Card';
import CardActions from '@material-ui/core/CardActions';
import CardContent from '@material-ui/core/CardContent';
import FavoriteIcon from '@material-ui/icons/Favorite';
import ShareIcon from '@material-ui/icons/Share';
import IconButton from '@material-ui/core/IconButton';
import Typography from '@material-ui/core/Typography';
import Grid from '@material-ui/core/Grid';
import Rating from '@material-ui/lab/Rating';
import Button from '@material-ui/core/Button';
import Snackbar from '@material-ui/core/Snackbar';
import CloseIcon from '@material-ui/icons/Close';
import { UserContext } from '../../Context/UserContext/UserContext';
import Share from '../../Components/Share/Share';
import ShoppingCartIcon from '@material-ui/icons/ShoppingCart';
import Badge from '@material-ui/core/Badge';
import Tooltip from '@material-ui/core/Tooltip';

import './CustomCard.css';

const useStyles = makeStyles({
  root: {
    width: 275,
    marginBottom: 20,
    transition: 'all 200ms',
    '&:hover': {
      boxShadow: '0 0 4px silver',
    },
  },
  bullet: {
    display: 'inline-block',
    margin: '0 2px',
    transform: 'scale(0.8)',
  },
  image: {
    maxWidth: '100%',
  },
  content: {
    paddingBottom: 0,
    '&:hover': {
      cursor: 'pointer',
    },
  },
  liked: {
    color: '#ff6d75',
  },
  incart: {
    color: '#ff6d75',
  },
});

const StyledRating = withStyles({
  iconFilled: {
    color: '#ff6d75',
  },
  iconHover: {
    color: '#ff3d47',
  },
})(Rating);

function arrayRemove(arr, value) { 
  return arr.filter(function(ele){ 
      return ele != value; 
  });
}

function CustomCard(props) {
  const classes = useStyles();
  const [user, setUser] = useContext(UserContext);
  const [inCart, setInCart] = useState(user.cart.includes(props.product));
  const [liked, setLiked] = useState(false);
  const [snack, setSnack] = useState(false);
  const [snackMsg, setSnackMsg] = useState('hello');
  const [redirect, setRedirect] = useState('');

  const handleLike = () => {
    setSnackMsg(liked?'Removed from liked':'Added to liked');
    setSnack(true);
    setLiked(!liked);
  };

  const handleSnack = () => {
    setSnack(!snack);
  };

  const doRedirect = (url) => {
    setRedirect(url);
  };

  const handleCart = (ID) => {
    setSnackMsg(inCart?'Removed from cart':'Added to cart');
    setSnack(true);
    if (inCart) {
    const newCart = arrayRemove(user.cart, props.product);
    setUser(prevUser=>({
      ...prevUser,
      cart: newCart,
    }));
    setInCart(false);
  }else {
    const newCart = [...user.cart, props.product];
    setUser(prevUser=>({
      ...prevUser,
      cart: newCart,
    }));
    setInCart(true);
  }
  };

  return (
    <Card className={classes.root} variant="outlined">
      <CardContent className={classes.content} onClick={()=>{doRedirect("/product/"+props.product.id)}}>
        <Grid container align='left' justify="start">
          <Grid item xs>
            <Typography color="textSecondary" gutterBottom>
              {props.product.type}
            </Typography>
          </Grid>
          <Grid item>
            <StyledRating
              name="customized-color"
              value={props.product.rating}
              getLabelText={(value) => `${value} Heart${value !== 1 ? 's' : ''}`}
              precision={0.5}
              icon={<FavoriteIcon fontSize="inherit" />}
              disabled
            />
          </Grid>
        </Grid>
        <img src={'https://external-content.duckduckgo.com/iu/?u=https%3A%2F%2Fukmadcat.com%2Fwp-content%2Fuploads%2F2019%2F04%2Fsleepy-cat.jpg&f=1&nofb=1'} className={classes.image} alt="" />
        <Grid container alignItems="center">
          <Grid item xs align='left'>
            <Typography gutterBottom variant="h6">
              {props.product.name}
            </Typography>
          </Grid>
          <Grid item>
            <Typography gutterBottom variant="h6">
              {props.product.price}$
            </Typography>
          </Grid>
        </Grid>
        <Typography color="textSecondary" align='left'>
          {props.product.subname}
        </Typography>
      </CardContent>
      <CardActions disableSpacing>
        <Grid container align="left">
          <Grid item xs>
          <Tooltip title="Add" aria-label="add"></Tooltip>
          <IconButton
              aria-label="add to favorites"
              onClick={user.loggedIn?(()=>handleCart(props.product.id)):(()=>doRedirect("/login"))}
            >
              <Badge color="secondary" variant="dot" invisible={!inCart}>
              <ShoppingCartIcon />

        </Badge>
            </IconButton>
          </Grid>
          <Grid item>
            <IconButton
              aria-label="add to favorites"
              onClick={handleLike}
            >
              <FavoriteIcon className={liked ? classes.liked : ''} />
            </IconButton>
            <IconButton aria-label="share">
              <ShareIcon />
            </IconButton>
          </Grid>
        </Grid>
      </CardActions>

      <Snackbar
        anchorOrigin={{
          vertical: 'bottom',
          horizontal: 'center',
        }}
        open={snack}
        autoHideDuration={2000}
        message={snackMsg}
        action={
          <React.Fragment>
            <IconButton size="small" aria-label="close" color="inherit" onClick={handleSnack}>
              <CloseIcon fontSize="small" />
            </IconButton>
          </React.Fragment>
        }
      />
      {redirect ? (<Redirect push to={redirect} />) : ''}
      <Share/>
    </Card>
  );
}
export default CustomCard;
