const StyledBadge = withStyles((theme) => ({
    badge: {
        right: -3,
        top: 13,
        border: `2px solid ${theme.palette.background.paper}`,
        padding: '0 4px',
    },
}))(Badge);

function Navigation() {
    const classes = useStyles();
    const location = useLocation();

    const [user, setUser] = useContext(UserContext);
    const [open, setOpen] = useState(false);

    const handleDrawerOpen = () => {
        setOpen(true);
    };

    const handleDrawerClose = () => {
        setOpen(false);
    };

    const handleLogOut = () => {
        setUser({
            loggedIn: false,
            username: '',
            password: '',
            liked: [],
            cart: [],
        });
    };

    return (
        <div className={classes.root}>
            <AppBar
                position="sticky"
                className={clsx(classes.appBar, {
                    [classes.appBarShift]: open,
                })}>
                <Toolbar>
                    <IconButton
                        edge="start"
                        className={clsx(classes.menuButton, open && classes.hide)}
                        onClick={handleDrawerOpen}
                        color="inherit"
                        aria-label="open drawer"
                    >
                        <MenuIcon />
                    </IconButton>
                    <Typography className={classes.title} variant="h6" noWrap>
                        Web Shop
            </Typography>

                    <div
                        className={classes.search}
                        onClick={handleDrawerClose}>
                            <div className={classes.searchIcon}>
                            <SearchIcon />
                        </div>
                        <InputBase
                            placeholder="Searchâ€¦"
                            classes={{
                                root: classes.inputRoot,
                                input: classes.inputInput,
                            }}
                            inputProps={{ 'aria-label': 'search' }}
                        />

                    </div>
                </Toolbar>
            </AppBar>
            <React.Fragment>
            <Drawer
                className={classes.drawer}
                variant="persistent"
                anchor="left"
                open={open}
                classes={{
                    paper: classes.drawerPaper,
                }}
            >
                <div className={classes.drawerHeader}>

                    <Grid container alignItems="center">
                        <Grid item xs>
                            {user.loggedIn ?
                                <ListItem>
                                    <ListItemIcon>
                                        <Avatar src="/broken-image.jpg" />
                                    </ListItemIcon>
                                    <ListItemText
                                        primary={user.username}
                                    />
                                </ListItem>
                                : ''}
                        </Grid>
                        <Grid item>
                            <IconButton onClick={handleDrawerClose}>
                                <ChevronRightIcon />
                            </IconButton>
                        </Grid>
                    </Grid>
                </div>

                <Divider />

                <List>
                    <Link to="/" onClick={handleDrawerClose}>
                        <ListItem button selected={(location.pathname === '/')}>
                            <ListItemIcon><HomeIcon /></ListItemIcon>
                            <ListItemText
                                primary={'Home'}

                            />
                        </ListItem>

                    </Link>
                    <Link to="/products" onClick={handleDrawerClose}>
                        <ListItem button selected={(location.pathname === '/products')}>
                            <ListItemIcon><BallotIcon /></ListItemIcon>
                            <ListItemText
                                primary={'Products'}
                            />
                        </ListItem>
                    </Link>
                </List>
                <Divider />
                <List>
                {user.loggedIn ?
                <Link to="/" onClick={handleLogOut}>
                <ListItem button>
                    <ListItemIcon><PersonIcon /></ListItemIcon>
                    <ListItemText
                        primary={'Log Out'}
                    />
                </ListItem>
            </Link>
            :
                    <Link to="/login" onClick={handleDrawerClose}>
                        <ListItem button selected={(location.pathname === '/login')}>
                            <ListItemIcon><PersonIcon /></ListItemIcon>
                            <ListItemText
                                primary={'Log In'}
                            />
                        </ListItem>
                    </Link>
                }
                {user.loggedIn ? '':
                    <Link to="/register" onClick={handleDrawerClose}>
                        <ListItem button selected={(location.pathname === '/register')}>
                            <ListItemIcon><PersonAddIcon /></ListItemIcon>
                            <ListItemText
                                primary={'Register'}
                            />
                        </ListItem>
                    </Link>
            }
            {user.loggedIn?
                    <Link to={user.loggedIn?"/mycart":"/login"} onClick={handleDrawerClose}>
                        <ListItem button selected={(location.pathname === '/mycart')}>
                            <ListItemIcon>
                                <StyledBadge badgeContent={user.cart.length} color="primary">
                                    <ShoppingCartIcon />
                                </StyledBadge>
                            </ListItemIcon>
                            <ListItemText
                                primary={'My Cart'}
                            />
                        </ListItem>
                    </Link>
                    : ''}
                {user.loggedIn?
                    <Link to={user.loggedIn?"/myliked":"/login"} onClick={handleDrawerClose}>
                        <ListItem button selected={(location.pathname === '/myliked')}>
                            <ListItemIcon>
                            <StyledBadge badgeContent={user.liked.length} color="primary">
                                    <FavoriteIcon />
                                    </StyledBadge>
                            </ListItemIcon>
                            <ListItemText
                                primary={'Liked'}
                            />
                        </ListItem>
                    </Link>
                    : ''}
                </List>
                <Divider />
                <List>
                    <Link to="/rubrics" onClick={handleDrawerClose}>
                        <ListItem button selected={(location.pathname === '/rubrics')}>
                            <ListItemIcon><InfoIcon /></ListItemIcon>
                            <ListItemText
                                primary={'Rubrics'}
                            />
                        </ListItem>
                    </Link>
                    <Link to="/references" onClick={handleDrawerClose}>
                        <ListItem button selected={(location.pathname === '/references')}>
                            <ListItemIcon><FormatQuoteIcon /></ListItemIcon>
                            <ListItemText
                                primary={'References'}
                            />
                        </ListItem>
                    </Link>
                </List>
                <Divider />
                <List>
                    <Link to="/preferences" onClick={handleDrawerClose}>
                        <ListItem button selected={(location.pathname === '/preferences')}>
                            <ListItemIcon><SettingsIcon /></ListItemIcon>
                            <ListItemText
                                primary={'Preferences'}
                            />
                        </ListItem>
                    </Link>
                </List>
            </Drawer>

            </React.Fragment>
        </div >
    );
}

export default withRouter(Navigation);
