import React, { useContext, useState } from 'react';
import { UserContext } from '../../Context/UserContext/UserContext';
import CustomCard from '../../Components/CustomCard/CustomCard';
import Container from '@material-ui/core/Container';
import Grid from '@material-ui/core/Grid';
import { fade, makeStyles, withStyles } from '@material-ui/core/styles';
import InfoTable from '../../Components/InfoTable/InfoTable';
import Typography from '@material-ui/core/Typography';
import Button from '@material-ui/core/Button';


import Table from '@material-ui/core/Table';
import TableBody from '@material-ui/core/TableBody';
import TableCell from '@material-ui/core/TableCell';
import TableContainer from '@material-ui/core/TableContainer';
import TableHead from '@material-ui/core/TableHead';
import TableRow from '@material-ui/core/TableRow';
import Paper from '@material-ui/core/Paper';
import List from '@material-ui/core/List';
import ListItem from '@material-ui/core/ListItem';
import ListItemText from '@material-ui/core/ListItemText';



const useStyles = makeStyles((theme) => ({
    root: {
        display: 'grid',


        alignItems: 'center',
        height: '80vh',
    },
    table: {
        minWidth: 300,
    },
    listItem: {
        padding: theme.spacing(1, 0),
    },
    total: {
        fontWeight: 700,
    },
    title: {
        marginTop: theme.spacing(2),
    },
}));

function calcTotalPrice(products) {
    var total = 0;
    console.log("AAAA");
    for (var i = 0; i < products.length; i++) {
        total += products[i].price;
        console.log(total);
    }
    return total;
}

const Cart = (props) => {
    const classes = useStyles();
    const [user] = useContext(UserContext);


    return (
        <Container maxWidth='md' className={classes.root}>

            <Paper >
                {user.cart.length > 0 ?

                    <React.Fragment>
                        <Typography variant="h6" gutterBottom>
                            Order summary
      </Typography>
                        <List disablePadding>
                            {user.cart.map((product) => (
                                <ListItem className={classes.listItem} key={product.name}>
                                    <ListItemText primary={product.name} secondary={"aaa"} />
                                    <Typography variant="body2">{product.price}</Typography>
                                </ListItem>
                            ))}
                            <ListItem className={classes.listItem}>
                                <ListItemText primary="Total" />
                                <Typography variant="subtitle1" className={classes.total}>
                                    $34.06
          </Typography>
                            </ListItem>
                        </List>
                        <Grid container spacing={2}>
                            <Grid item xs={12} sm={6}>
                                <Typography variant="h6" gutterBottom className={classes.title}>
                                    Shipping
          </Typography>
                                <Typography gutterBottom>John Smith</Typography>
                                <Typography gutterBottom>{"addresses.join(', ')"}</Typography>
                            </Grid>

                        </Grid>
                    </React.Fragment>
                    :
                    <div>
                        <Typography variant='h4'>Your cart seems a bit empty</Typography>
                    </div>
                }

            </Paper>
            {user.cart.length > 0 ?
                <Grid item align='right'>
                    <Button variant="contained" color="primary">Proceed to checkout</Button>
                </Grid>
                : ''}
        </Container>

    );

}

export default Cart;



function createData(name, calories, fat, carbs, protein) {
    return { name, calories, fat, carbs, protein };
}

const rows = [
    createData('Frozen yoghurt', 159, 6.0, 24, 4.0),
    createData('Ice cream sandwich', 237, 9.0, 37, 4.3),
    createData('Eclair', 262, 16.0, 24, 6.0),
    createData('Cupcake', 305, 3.7, 67, 4.3),
    createData('Gingerbread', 356, 16.0, 49, 3.9),
];
