import React from 'react';
import { makeStyles } from '@material-ui/core/styles';

import CssBaseline from '@material-ui/core/CssBaseline';
import Grid from '@material-ui/core/Grid';
import Typography from '@material-ui/core/Typography';
import Container from '@material-ui/core/Container';
import Slider from '@material-ui/core/Slider';
import Paper from '@material-ui/core/Paper';


const useStyles = makeStyles({
    root: {
        height: '100vh',
    },
    item: {
        width: '100%',
    },
    paper: {
        padding: '2rem',
        width: '100%',
        maxWidth: '800px',
        minWidth: '200px',
    },
    header: {
        marginBottom: '2rem',
    },
    value: {
        display: 'flex',
        alignItems: 'center',
    },
});

function Preferences() {
    const classes = useStyles();
    const [valueR, setValueR] = React.useState(0);
    const [valueG, setValueG] = React.useState(0);
    const [valueB, setValueB] = React.useState(0);

    const handleChangeR = (event, newValue) => {
        setValueR(newValue);
    };
    const handleChangeG = (event, newValue) => {
        setValueG(newValue);
    };
    const handleChangeB = (event, newValue) => {
        setValueB(newValue);
    };

    return (
        <Container>
            <CssBaseline />

            <Grid
                container
                direction="row"
                justify="center"
                alignItems="center"
                className={classes.root}
            >
                <Paper elevation={3} className={classes.paper}>
                    <Typography variant="h5" component="h2" className={classes.header}>
                        Preferences
                    </Typography>
                    <Grid item className={classes.item}>


                        <Grid container spacing={2}>
                            <Grid item className={classes.value}>
                                <Typography gutterBottom>
                                    Red   {valueR}
                                </Typography>
                            </Grid>
                            <Grid item xs>
                                <Slider value={valueR} max={255} onChange={handleChangeR} />
                            </Grid>
                        </Grid>
                        <Grid container spacing={2}>
                            <Grid item className={classes.value}>
                                <Typography gutterBottom>
                                    Green {valueG}
                                </Typography>
                            </Grid>
                            <Grid item xs>
                                <Slider value={valueG} max={255} onChange={handleChangeG} />
                            </Grid>
                        </Grid>

                        <Grid container spacing={2}>
                            <Grid item className={classes.value}>
                                <Typography gutterBottom>
                                    Blue  {valueB}
                                </Typography>
                            </Grid>
                            <Grid item xs>
                                <Slider value={valueB} max={255} onChange={handleChangeB} />
                            </Grid>
                        </Grid>
                    </Grid>
                </Paper>
            </Grid>

        </Container>
    );
}

export default Preferences;
