import React, {Component} from 'react';
import './Button.css';

const value = 'Back';

class Button extends Component {
  constructor(props) {
    super(props);
    this.state = {
      newValue: props.value,
      oldValue: props.value

    };
  }
  render() {
    return (
      <button
      type="button"
      className="btn btn-primary mx-2"
      onClick={()=>this.setState({newValue: this.state.newValue===value?this.state.oldValue:value})}
      >
        {this.state.newValue}
      </button>
    );  
  }
}

export default Button;
