import React, { Component } from 'react';
import { Link, withRouter } from "react-router-dom";
import './Navigation.css';

class Navigation extends Component {
    constructor(props) {
        super(props);
        this.state = { collapsed: true };
    }
    render() {
        return (
            <nav className="navbar navbar-expand-lg navbar-light bg-light">
                <div className="container-fluid container">
                    <Link className="navbar-brand" to="/">Navbar</Link>
                    <button
                        className="navbar-toggler"
                        type="button"
                        onClick={() => this.setState({ collapsed: !this.state.collapsed })}
                    >
                        <a>X</a>
                    </button>
                    <div
                        className={`navbar-collapse ${this.state.collapsed ? 'collapse' : ''}`}
                        id="navbar">
                        <ul className="navbar-nav ml-auto my-2 my-lg-0 navbar-nav-scroll">
                            <li className="nav-item">
                                <Link
                                    className=
                                    {`
                                    nav-link text-center
                                    ${this.props.location.pathname === "/" ? "active" : ""}
                                `}
                                    to="/">
                                    Landing
                            </Link>
                            </li>
                            <li className="nav-item">
                                <Link
                                    className=
                                    {`
                                    nav-link text-center
                                    ${this.props.location.pathname === "/products" ? "active" : ""}
                                `}
                                    to="/products">
                                    Products
                            </Link>
                            </li>
                            <li className="nav-item">
                                <Link
                                    className=
                                    {`
                                    nav-link text-center
                                    ${this.props.location.pathname === "/about" ? "active" : ""}
                                `}
                                    to="/about">
                                    About
                            </Link>
                            </li>
                        </ul>
                    </div>
                </div>
            </nav>
        );
    }
}

export default withRouter(Navigation);
