import React, { useState, useContext } from 'react';
import { makeStyles } from '@material-ui/core/styles';

import CssBaseline from '@material-ui/core/CssBaseline';
import Grid from '@material-ui/core/Grid';
import Typography from '@material-ui/core/Typography';
import Container from '@material-ui/core/Container';
import Paper from '@material-ui/core/Paper';
import Main from '../../Components/Main/Main';
import { Switch } from '@material-ui/core';
import { CustomThemeContext } from '../../Context/CustomThemeContext/CustomThemeContext';

const useStyles = makeStyles((theme) => ({
    root: {
        display: 'grid',
        alignItems: 'center',
        height: '80vh',
    },
    paper: {
        padding: '2rem 1rem 1rem 1rem',
    },
    table: {
        minWidth: 300,
    },
    listItem: {
        padding: theme.spacing(1, 0),
    },
    total: {
        fontWeight: 700,
    },
    title: {
        marginTop: theme.spacing(2),
    },
    noDecoration: {
        textDecoration: 'none',
        color: theme.palette.text.primary,
    },
}));

function Preferences() {
    const [darkMode, setDarkMode, theme] = useContext(CustomThemeContext);

    const handleDark = () => {
        setDarkMode(darkMode);
        setDarkMode(oldDark => {
            return !oldDark;
        });
    }

    return (
        <Main>
            <Typography variant="h5" component="h2" >
                Preferences
            </Typography>
            <Switch
                checked={darkMode}
                onChange={handleDark}
                color="primary"
                name="checkedB"
                inputProps={{ 'aria-label': 'primary checkbox' }}
            />
            <React.Fragment>
                        <Typography variant="h6" className={classes.total} gutterBottom>
                            Order summary
      </Typography>
                        <List disablePadding>
                            {user.cart.map((product) => (
                                <ListItem className={classes.listItem} key={product.name}>
                            <ListItemText primary={<Link className={classes.noDecoration} to={'/product/'+product.id}>{product.name}</Link>}
                                    secondary={
                                    <Button size='small' color="secondary" onClick={() => { handleCart(product) }}>REMOVE</Button>
                                    } />
                                    <Typography variant="body2">$ {product.price}</Typography>
                                </ListItem>
                            ))}
                            <ListItem className={classes.listItem}>
                                <ListItemText primary="Total:" />
                                <Typography variant="subtitle1" className={classes.total}>

                                </Typography>
                            </ListItem>
                        </List>
                        <Grid item align='right'>
                            <Button variant="contained" color="primary">Proceed to checkout</Button>
                        </Grid>
                    </React.Fragment>
        </Main>
    );
}

export default Preferences;
