import React, { useContext, useState } from 'react';
import { UserContext } from '../../Context/UserContext/UserContext';
import CustomCard from '../../Components/CustomCard/CustomCard';
import Container from '@material-ui/core/Container';
import Grid from '@material-ui/core/Grid';
import { fade, makeStyles, withStyles } from '@material-ui/core/styles';
import InfoTable from '../../Components/InfoTable/InfoTable';
import Typography from '@material-ui/core/Typography';
import Button from '@material-ui/core/Button';


import Table from '@material-ui/core/Table';
import TableBody from '@material-ui/core/TableBody';
import TableCell from '@material-ui/core/TableCell';
import TableContainer from '@material-ui/core/TableContainer';
import TableHead from '@material-ui/core/TableHead';
import TableRow from '@material-ui/core/TableRow';
import Paper from '@material-ui/core/Paper';

const useStyles = makeStyles((theme) => ({
    root: {
        display: 'grid',


        alignItems: 'center',
        height: '80vh',
    },
    table: {
        minWidth: 300,
    },
}));

function calcTotalPrice(products) {
    var total = 0;
    console.log("AAAA");
    for (var i=0; i<products.length; i++) {
        total += products[i].price;
        console.log(total);
    }
    return total;
}

const Cart = (props) => {
    const classes = useStyles();
    const [user] = useContext(UserContext);


    return (
        <Container maxWidth='md' className={classes.root}>

            <TableContainer component={Paper}>
            {user.cart.length>0?
                <Table className={classes.table} size="small" aria-label="a dense table">
                    <TableHead>
                        <TableRow>
                            <TableCell>Your Cart</TableCell>
                            <TableCell align="right">Price</TableCell>
                        </TableRow>
                    </TableHead>
                    <TableBody>
                    {user.cart.map(product => (
                    <TableRow key={product.name}>
                    <TableCell component="th" scope="row">{product.name}</TableCell>
                    <TableCell align="right">{product.price} $</TableCell>
                </TableRow>
                ))}
                <TableRow key='total'>
                    <TableCell component="th" scope="row">
                        Total:
                    </TableCell>
                    <TableCell align="right">{calcTotalPrice(user.cart)} $</TableCell>
                </TableRow>

                    </TableBody>
                </Table>
            :  
            <div>
                <Typography variant='h4'>Your cart seems a bit empty</Typography>
            </div>
            }

            </TableContainer>
            {user.cart.length>0?
            <Grid item align='right'>
            <Button variant="contained" color="primary">Proceed to checkout</Button>
            </Grid>
            : ''}
        </Container>

    );

}

export default Cart;



function createData(name, calories, fat, carbs, protein) {
    return { name, calories, fat, carbs, protein };
}

const rows = [
    createData('Frozen yoghurt', 159, 6.0, 24, 4.0),
    createData('Ice cream sandwich', 237, 9.0, 37, 4.3),
    createData('Eclair', 262, 16.0, 24, 6.0),
    createData('Cupcake', 305, 3.7, 67, 4.3),
    createData('Gingerbread', 356, 16.0, 49, 3.9),
];
