import React, { Component, useState, useContext } from 'react';
import CustomCard from '../../Components/CustomCard/CustomCard';
import ItemsGrid from '../../Components/ItemsGrid/ItemsGrid';
import { ProductContext } from '../../Context/ProductContext/ProductContext';
import { UserContext } from '../../Context/UserContext/UserContext';

import Container from '@material-ui/core/Container';
import Typography from '@material-ui/core/Typography';
import Grid from '@material-ui/core/Grid';
import Paper from '@material-ui/core/Paper';
import { fade, makeStyles, withStyles } from '@material-ui/core/styles';
import Background from '../../Components/Background/Background'
import Main from '../../Components/Main/Main';
import ButtonGroup from '@material-ui/core/ButtonGroup';
import Button from '@material-ui/core/Button';

import './Landing.css';
import { Link, withRouter, useLocation } from "react-router-dom";

const useStyles = makeStyles((theme) => ({
    root: {
        padding: '2rem 1rem',
    },
    section: {
        padding: '1rem',
    },
    signup: {
        height: '70vh',
        width: '100%',
        display: 'flex',
        justifyContent: 'center',
        alignItems: 'center',
        '&>*': {
            height: '50%',
        }
    },
    content: {
        marginBottom: '2rem',
        textAlign: 'center',
    },
    button: {
        marginRight: '1rem',
    },
    noDecoration: {
        textDecoration: 'none',
    }
}));

function Landing(props) {
    const classes = useStyles();

    const [products, setProducts] = useContext(ProductContext);
    const [user, setUser] = useContext(UserContext);

    return (
        <Container className={classes.root}>
            {user.loggedIn?'':
            <Grid>
                <Grid item className={classes.signup} align="center">
                    <Grid item >
                        <Grid item className={classes.content}>
                            <Typography variant="h5" color='primary'>
                                Hi, there!
                            </Typography>
                            <Typography variant="p">
                                To experience all the advantages, please consider making an account.
                            </Typography>
                        </Grid>
                        <Grid item xs>
                            <Link to="/login" className={classes.noDecoration}>
                            <Button
                                color="primary"
                                className={classes.button}
                            >Log In</Button>
                            </Link>
                            <Link to="/register" className={classes.noDecoration}>
                            <Button
                                variant="contained"
                                color="primary"
                                className={classes.spacer}
                            >Register</Button>
                            </Link>
                        </Grid>
                    </Grid>
                </Grid>
            </Grid>
            }
            <Grid>
                <Grid item className={classes.signup} align="center">
                    <Grid item align="center">
                        <Grid item className={classes.content}>
                            <Typography variant="h5" color='primary'>
                                Check us out
                            </Typography>
                            <Typography variant="p">
                                Experience our ever growing offers, with more than <strong>2 products</strong> (its 3 in total) ready to ship at any moment.
                            </Typography>
                        </Grid>

                        <Grid item xs>
                        <Link to="/products" className={classes.noDecoration}>
                            <Button
                                variant="contained"
                                color="primary"

                                className={classes.spacer}
                            >
                                see products
                                                </Button>
</Link>
                        </Grid>
                    </Grid>
                </Grid>
            </Grid>
            <Grid>
                <Grid item className={classes.signup} align="center">
                    <Grid container align="center" maxWidth=''>

                        <Grid item className={classes.content} md={3}>
                            <Typography variant="h5" color='primary'>
                                Fastest Shipping
                            </Typography>
                            <Typography variant="p">
                                We ship out every order within an hour from receiving it. Never wait a month for your product ever again!
                            </Typography>
                        </Grid>

                        <Grid item className={classes.content} md={3}>
                            <Typography variant="h5" color='primary'>
                                Money Back Guarantee
                            </Typography>
                            <Typography variant="p">
                                You will be refunded for every product that does not match the description.
                            </Typography>
                        </Grid>

                        <Grid item className={classes.content} md={3}>
                            <Typography variant="h5" color='primary'>
                            </Typography>
                            <Typography variant="p">
                            </Typography>
                        </Grid>
                    </Grid>
                </Grid>
            </Grid>
        </Container>
    );
}

export default withRouter(Landing);
