import React from 'react';
import { createMuiTheme, makeStyles } from '@material-ui/core/styles';

import CssBaseline from '@material-ui/core/CssBaseline';
import Grid from '@material-ui/core/Grid';
import Typography from '@material-ui/core/Typography';
import Container from '@material-ui/core/Container';
import Slider from '@material-ui/core/Slider';

export const theme = createMuiTheme({
    palette: {
        primary: {
            main: 'rgba(255, 0, 0, 1)',
        },
        secondary: {
            main: 'rgba(255, 0, 0, 1)',
        },
    },
});

const useStyles = makeStyles({
    root: {
        height: '100vh',
    },
    item: {
        width: '100%',
        maxWidth: '800px',
        minWidth: '200px',
    },
});

function Preferences() {
    const classes = useStyles();
    const [valueR, setValueR] = React.useState(0);
    const [valueG, setValueG] = React.useState(0);
    const [valueB, setValueB] = React.useState(0);

    const handleChangeR = (event, newValue) => {
        setValueR(newValue);
    };
    const handleChangeG = (event, newValue) => {
        setValueG(newValue);
    };
    const handleChangeB = (event, newValue) => {
        setValueB(newValue);
    };

    return (
        <Container>
            <CssBaseline />
            <Paper elevation={3} />
            <Grid
                container
                direction="row"
                justify="center"
                alignItems="center"
                className={classes.root}
            >
                <Grid item className={classes.item}>
                    <Typography gutterBottom>
                        Red {valueR}
                    </Typography>
                    <Slider value={valueR} max={255} onChange={handleChangeR} />
                    <Typography gutterBottom>
                        Green {valueG}
                    </Typography>
                    <Slider value={valueG} max={255} onChange={handleChangeG} />
                    <Typography gutterBottom>
                        Blue {valueB}
                    </Typography>
                    <Slider value={valueB} max={255} onChange={handleChangeB} />
                </Grid>
            </Grid>
        </Container>
    );
}

export default Preferences;
