import React, {useState} from 'react';
import { makeStyles, withStyles } from '@material-ui/core/styles';
import Card from '@material-ui/core/Card';
import CardActions from '@material-ui/core/CardActions';
import CardContent from '@material-ui/core/CardContent';
import FavoriteIcon from '@material-ui/icons/Favorite';
import ShareIcon from '@material-ui/icons/Share';
import IconButton from '@material-ui/core/IconButton';
import Typography from '@material-ui/core/Typography';
import Grid from '@material-ui/core/Grid';
import Rating from '@material-ui/lab/Rating';
import Button from '@material-ui/core/Button';
import Snackbar from '@material-ui/core/Snackbar';
import CloseIcon from '@material-ui/icons/Close';

import './CustomCard.css';

const useStyles = makeStyles({
  root: {
    width: 275,
    marginBottom: 20,
    transition: 'all 200ms',

  },
  bullet: {
    display: 'inline-block',
    margin: '0 2px',
    transform: 'scale(0.8)',
  },
  image: {
    maxWidth: '100%',
  },
  content: {
    paddingBottom: 0,
    '&:hover': {
      cursor: 'pointer',
    },
  },
  liked: {
    color: '#ff6d75',
  },
});

const StyledRating = withStyles({
  iconFilled: {
    color: '#ff6d75',
  },
  iconHover: {
    color: '#ff3d47',
  },
})(Rating);


function SimpleSnackbar() {
  const [open, setOpen] = React.useState(false);

  const handleClick = () => {
    setOpen(true);
  };

  const handleClose = (event, reason) => {
    if (reason === 'clickaway') {
      return;
    }

    setOpen(false);
  };

  return (
    <div>
      <Button onClick={handleClick}>Open simple snackbar</Button>
      <Snackbar
        anchorOrigin={{
          vertical: 'bottom',
          horizontal: 'left',
        }}
        open={open}
        autoHideDuration={6000}
        onClose={handleClose}
        message="Note archived"
        action={
          <React.Fragment>
            <Button color="secondary" size="small" onClick={handleClose}>
              UNDO
            </Button>
            <IconButton size="small" aria-label="close" color="inherit" onClick={handleClose}>
              <CloseIcon fontSize="small" />
            </IconButton>
          </React.Fragment>
        }
      />
    </div>
  );
}

function CustomCard(props) {
  const classes = useStyles();
  const [liked, setLiked] = useState(false);
  const handleLike = () => {
    setLiked(!liked);
  }
  return (
    <Card className={classes.root} variant="outlined">
      <CardContent className={classes.content}>
        <Grid container alignItems="center">
          <Grid item xs>
            <Typography color="textSecondary" gutterBottom>
              {props.product.type}
            </Typography>
          </Grid>
          <Grid item>
          <StyledRating
          name="customized-color"
          value={props.product.rating}
          getLabelText={(value) => `${value} Heart${value !== 1 ? 's' : ''}`}
          precision={0.5}
          icon={<FavoriteIcon fontSize="inherit" />}
          disabled
        />
          </Grid>
        </Grid>
        <img src={'https://external-content.duckduckgo.com/iu/?u=https%3A%2F%2Fukmadcat.com%2Fwp-content%2Fuploads%2F2019%2F04%2Fsleepy-cat.jpg&f=1&nofb=1'} className={classes.image} alt="" />
        <Grid container alignItems="center">
          <Grid item xs>
            <Typography gutterBottom variant="h6">
              {props.product.name}
            </Typography>
          </Grid>
          <Grid item>
            <Typography gutterBottom variant="h6">
            {props.product.price}$
            </Typography>
          </Grid>
        </Grid>
        <Typography color="textSecondary">
        {props.product.subname}
        </Typography>
      </CardContent>
      <CardActions disableSpacing>
        <Grid container alignItems="center">
          <Grid item xs>
            <Button color="primary" onClick={()=>{alert("Button")}}>Add to cart</Button>
          </Grid>
          <Grid item>
            <IconButton
            aria-label="add to favorites"
            onClick={handleLike}
            >
              <FavoriteIcon className={liked?classes.liked:''} />
            </IconButton>
            <IconButton aria-label="share">
              <ShareIcon />
            </IconButton>
          </Grid>
        </Grid>
      </CardActions>
      {SimpleSnackbar()}
    </Card>
  );
}
export default CustomCard;
